interface CommitmentMessageUseCase {
    suspend fun json(nonce: String): String
}

class CommitmentMessageUseCaseImpl(private val secretKeyUseCase: SecretKeyUseCase) :
    CommitmentMessageUseCase {

    override suspend fun json(nonce: String): String {
        val issuerPkXml = "<?xml version=\"1.0\" encoding=\"UTF-8\" standalone=\"no\"?>\n" +
                "<IssuerPublicKey xmlns=\"http://www.zurich.ibm.com/security/idemix\"><Counter>0</Counter><ExpiryDate>1643236269</ExpiryDate><Elements><n></n><Z></Z><S></S><Bases num=\"12\"><Base_0></Base_0><Base_1></Base_1><Base_2></Base_2><Base_3></Base_3><Base_4></Base_4><Base_5></Base_5><Base_6></Base_6><Base_7></Base_7><Base_8></Base_8><Base_9></Base_9><Base_10></Base_10><Base_11></Base_11></Bases></Elements><Features><Epoch length=\"432000\"/></Features></IssuerPublicKey>"
        val secretKey = secretKeyUseCase.json()
        return Clmobile.createCommitmentMessage(
            secretKey.toByteArray(),
            issuerPkXml.toByteArray(),
            nonce.toByteArray()
        ).successString()
    }
}
