package nl.rijksoverheid.ctr.your_events.utils

import com.squareup.moshi.Moshi
import com.squareup.moshi.adapters.PolymorphicJsonAdapterFactory
import com.squareup.moshi.kotlin.reflect.KotlinJsonAdapterFactory
import io.mockk.mockk
import nl.rijksoverheid.ctr.api.json.Base64JsonAdapter
import nl.rijksoverheid.ctr.api.json.JsonObjectJsonAdapter
import nl.rijksoverheid.ctr.api.json.LocalDateJsonAdapter
import nl.rijksoverheid.ctr.api.json.OffsetDateTimeJsonAdapter
import nl.rijksoverheid.ctr.holder.api.OriginTypeJsonAdapter
import nl.rijksoverheid.ctr.holder.api.RemoteCouplingStatusJsonAdapter
import nl.rijksoverheid.ctr.holder.api.RemoteTestStatusJsonAdapter
import nl.rijksoverheid.ctr.holder.get_events.models.RemoteEvent
import nl.rijksoverheid.ctr.holder.get_events.models.RemoteEventNegativeTest
import nl.rijksoverheid.ctr.holder.get_events.models.RemoteEventPositiveTest
import nl.rijksoverheid.ctr.holder.get_events.models.RemoteEventRecovery
import nl.rijksoverheid.ctr.holder.get_events.models.RemoteEventVaccination
import nl.rijksoverheid.ctr.holder.get_events.models.RemoteProtocol
import nl.rijksoverheid.ctr.holder.your_events.utils.RemoteEventHolderUtilImpl
import nl.rijksoverheid.ctr.holder.your_events.utils.YourEventsFragmentUtilImpl
import org.junit.Assert.assertEquals
import org.junit.Assert.assertFalse
import org.junit.Assert.assertTrue
import org.junit.Test
import org.junit.runner.RunWith
import org.koin.test.AutoCloseKoinTest
import org.robolectric.RobolectricTestRunner

@RunWith(RobolectricTestRunner::class)
class RemoteEventHolderUtilImplTest : AutoCloseKoinTest() {
    private val moshi = Moshi.Builder()
    .add(RemoteTestStatusJsonAdapter())
    .add(Base64JsonAdapter())
    .add(JsonObjectJsonAdapter())
    .add(OffsetDateTimeJsonAdapter())
    .add(LocalDateJsonAdapter())
    .add(OriginTypeJsonAdapter())
    .add(RemoteCouplingStatusJsonAdapter())
    .add(PolymorphicJsonAdapterFactory.of(
        RemoteEvent::class.java, "type")
        .withSubtype(RemoteEventPositiveTest::class.java, "positivetest")
        .withSubtype(RemoteEventRecovery::class.java, "recovery")
        .withSubtype(RemoteEventNegativeTest::class.java, "negativetest")
        .withSubtype(RemoteEventVaccination::class.java, "vaccination"))
    .add(KotlinJsonAdapterFactory()).build()

    @Test
    fun `recovery event data are parsed correctly`() {
        val data = "{\"signature\":\"\",\"payload\":\"\"}"

        val remoteUtil = RemoteEventHolderUtilImpl(moshi, mockk(), mockk(relaxed = true), YourEventsFragmentUtilImpl(mockk()))

        val holder = remoteUtil.holder(data.toByteArray(), "")

        assertEquals("1960-01-01", holder?.birthDate)
        assertEquals("Bob", holder?.firstName)
        assertEquals("Bouwer", holder?.lastName)
        assertEquals("De", holder?.infix)
    }

    @Test
    fun `two matching holders are not conflicting`() {
        val storedHolders = listOf(
            getHolderExample()
        )

        val incomingHolders = listOf(
            getHolderExample()
        )

        val remoteUtil = RemoteEventHolderUtilImpl(moshi, mockk(), mockk(), YourEventsFragmentUtilImpl(mockk()))

        assertFalse(remoteUtil.conflicting(storedHolders, incomingHolders))
    }

    @Test
    fun `two matching holders with different alphabet are not conflicting`() {
        val storedHolders = listOf(
            getHolderExample(
                firstName = "Giorgos",
                lastName = "Pap"
            )
        )

        val incomingHolders = listOf(
            getHolderExample(
                firstName = "Γιώργος",
                lastName = "Παπ"
            )
        )

        val remoteUtil = RemoteEventHolderUtilImpl(moshi, mockk(), mockk(), YourEventsFragmentUtilImpl(mockk()))

        assertFalse(remoteUtil.conflicting(storedHolders, incomingHolders))
    }

    @Test
    fun `two matching holders with one name starting with apostrophe are not conflicting`() {
        val storedHolders = listOf(
            getHolderExample()
        )

        val incomingHolders = listOf(
            getHolderExample(
                firstName = "ian",
                lastName = "'rijter"
            )
        )

        val remoteUtil = RemoteEventHolderUtilImpl(moshi, mockk(), mockk(), YourEventsFragmentUtilImpl(mockk()))

        assertFalse(remoteUtil.conflicting(storedHolders, incomingHolders))
    }

    @Test
    fun `two matching holders with different first name only are not conflicting`() {
        val storedHolders = listOf(
            getHolderExample()
        )

        val incomingHolders = listOf(
            getHolderExample(
                firstName = "ian",
                lastName = "'rijter"
            )
        )

        val remoteUtil = RemoteEventHolderUtilImpl(moshi, mockk(), mockk(), YourEventsFragmentUtilImpl(mockk()))

        assertFalse(remoteUtil.conflicting(storedHolders, incomingHolders))
    }

    @Test
    fun `two matching holders with different last name only are not conflicting`() {
        val storedHolders = listOf(
            getHolderExample()
        )

        val incomingHolders = listOf(
            getHolderExample(
                firstName = "jan",
                lastName = "'pijter"
            )
        )

        val remoteUtil = RemoteEventHolderUtilImpl(moshi, mockk(), mockk(), YourEventsFragmentUtilImpl(mockk()))

        assertFalse(remoteUtil.conflicting(storedHolders, incomingHolders))
    }

    @Test
    fun `two matching holders with different birth month are conflicting`() {
        val storedHolders = listOf(
            RemoteProtocol.Holder("De", "jan", "pijter", "1982-06-25")
        )

        val incomingHolders = listOf(
            RemoteProtocol.Holder("De", "jan", "pijter", "1982-05-25")
        )

        val remoteUtil = RemoteEventHolderUtilImpl(moshi, mockk(), mockk(), YourEventsFragmentUtilImpl(mockk()))

        assertTrue(remoteUtil.conflicting(storedHolders, incomingHolders))
    }

    @Test
    fun `two matching holders with different birth day are conflicting`() {
        val storedHolders = listOf(
            RemoteProtocol.Holder("De", "jan", "pijter", "1982-06-26")
        )

        val incomingHolders = listOf(
            RemoteProtocol.Holder("De", "jan", "pijter", "1982-06-25")
        )

        val remoteUtil = RemoteEventHolderUtilImpl(moshi, mockk(), mockk(), YourEventsFragmentUtilImpl(mockk()))

        assertTrue(remoteUtil.conflicting(storedHolders, incomingHolders))
    }

    @Test
    fun `two holders, with one having null name, are conflicting`() {
        val storedHolders = listOf(
            RemoteProtocol.Holder("De", "jan", null, "1982-06-26")
        )

        val incomingHolders = listOf(
            RemoteProtocol.Holder("De", "jan", "pijter", "1982-06-25")
        )

        val remoteUtil = RemoteEventHolderUtilImpl(moshi, mockk(), mockk(), YourEventsFragmentUtilImpl(mockk()))

        assertTrue(remoteUtil.conflicting(storedHolders, incomingHolders))
    }

    @Test
    fun `two holders, both with invalid birthdate, are not conflicting`() {
        val storedHolders = listOf(
            RemoteProtocol.Holder("De", "jan", "pijter", "1982--06-26")
        )

        val incomingHolders = listOf(
            RemoteProtocol.Holder("De", "jan", "pijter", "1982--06-25")
        )

        val remoteUtil = RemoteEventHolderUtilImpl(moshi, mockk(), mockk(), YourEventsFragmentUtilImpl(mockk()))

        assertFalse(remoteUtil.conflicting(storedHolders, incomingHolders))
    }

    @Test
    fun `two holders, both with obscured birthdate, are not conflicting`() {
        val storedHolders = listOf(
            RemoteProtocol.Holder("De", "jan", "pijter", "1982-XX-XX")
        )

        val incomingHolders = listOf(
            RemoteProtocol.Holder("De", "jan", "pijter", "1982-XX-XX")
        )

        val remoteUtil = RemoteEventHolderUtilImpl(moshi, mockk(), mockk(), YourEventsFragmentUtilImpl(mockk()))

        assertFalse(remoteUtil.conflicting(storedHolders, incomingHolders))
    }

    @Test
    fun `two holders, one with obscured birthdate and one with visible birthdate, are conflicting`() {
        val storedHolders = listOf(
            RemoteProtocol.Holder("De", "jan", "pijter", "1982-XX-XX")
        )

        val incomingHolders = listOf(
            RemoteProtocol.Holder("De", "jan", "pijter", "1982-01-01")
        )

        val remoteUtil = RemoteEventHolderUtilImpl(moshi, mockk(), mockk(), YourEventsFragmentUtilImpl(mockk()))

        assertTrue(remoteUtil.conflicting(storedHolders, incomingHolders))
    }

    private fun getHolderExample(
        firstName: String = "jan",
        lastName: String = "rijter"
    ) = RemoteProtocol.Holder("De", firstName, lastName, "1982-05-25")
}
